import React, { useState } from 'react';
import {
    View,
    StyleSheet,
    Alert,
} from 'react-native';
import { Product, ProductForm } from '../types/Product';
import ProductList from '../components/ProductList';
import AddProductButton from '../components/AddProductButton';
import ProductModal from '../components/ProductModal';

import { useTheme } from '../context/ThemeContext';

// Generate ID sederhana
const generateId = (): string => Math.random().toString(36).substr(2, 9);

export default function Products() {
    const [products, setProducts] = useState<Product[]>([
        {
            id: '1',
            name: 'iPhone 15 Pro Max',
            price: 999,
            imageUrl: 'data:image/jpeg;base64,',
            description: 'Latest iPhone with advanced camera'
        },
        {
            id: '2',
            name: 'Samsung Galaxy S24',
            price: 799,
            imageUrl: 'data:image/jpeg;base64,',
            description: 'Powerful Android smartphone'
        },
        {
            id: '3',
            name: 'MacBook Air M3',
            price: 1299,
            imageUrl: 'https://images.unsplash.com/photo-1541807084-5c52b6b3adef?w=400',
            description: 'Lightweight and powerful laptop'
        },
        {
            id: '4',
            name: 'drone',
            price: 1299,
            imageUrl: 'data:image/jpeg;base64,',
            description: 'Lightweight and powerful laptop'
        },
        {
            id: '5',
            name: 'Headphone',
            price: 899,
            imageUrl: 'data:image/jpeg;base64,',
            description: 'Lightweight and powerful laptop'
        },
        {
            id: '6',
            name: 'Redmi 13 Pro 5g',
            price: 688,
            imageUrl: 'data:image/jpeg;base64,',
            description: 'Lightweight and powerful laptop'
        },

    ]);

    const [modalVisible, setModalVisible] = useState(false);
    const { isDark } = useTheme();
    const styles = getStyles(isDark);

    const handleAddProduct = (productForm: ProductForm) => {
        const newProduct: Product = {
            ...productForm,
            id: generateId(),
            price: Number(productForm.price),
        };

        setProducts(prev => [...prev, newProduct]);
        setModalVisible(false);
        Alert.alert('Success', 'Product added successfully!');
    };

    const handleProductPress = (product: Product) => {
        Alert.alert(
            'Product Details',
            `${product.name}\n\nPrice: $${product.price}\n\n${product.description || 'No description'}`,
            [{ text: 'OK' }]
        );
    };

    return (
        <View style={styles.mainContainer}>

            <ProductList
                products={products}
                onProductPress={handleProductPress}
            />

            <AddProductButton onPress={() => setModalVisible(true)} />

            <ProductModal
                visible={modalVisible}
                onClose={() => setModalVisible(false)}
                onSubmit={handleAddProduct}
            />
        </View>
    );
}

const getStyles = (isDark: boolean) => StyleSheet.create({
    mainContainer: {
        flex: 1,

        paddingTop: 30
    },
});